  }
  let label = data.args["label"]
  let progress_value = data.args["value"]

  header_text.textContent = `${label}` + String.fromCharCode(160)
  document.documentElement.style
    .setProperty('--progress-value', '0')
  getComputedStyle(document.documentElement).getPropertyValue('--progress-value')
  Streamlit.setFrameHeight(350)
}

// Attach our `onRender` handler to Streamlit's render event.
Streamlit.events.addEventListener(Streamlit.RENDER_EVENT, onRender)
